class Beep:  def very_short():    _alarm = Pin(2, Pin.OUT)    _alarm.value(0)    time.sleep_ms(50)    _alarm.value(1)    time.sleep_ms(200)  def short():    _alarm = Pin(2, Pin.OUT)    _alarm.value(0)    time.sleep_ms(100)    _alarm.value(1)    time.sleep_ms(200)  def long():    _alarm = Pin(2, Pin.OUT)    _alarm.value(0)    time.sleep_ms(400)    _alarm.value(1)    time.sleep_ms(200)  def very_long():    _alarm = Pin(2, Pin.OUT)    _alarm.value(0)    time.sleep_ms(1000)    _alarm.value(1)    time.sleep_ms(200)  def init():    _alarm = Pin(2, Pin.OUT)    _alarm.value(0)    time.sleep_ms(100)    _alarm.value(1)    time.sleep_ms(200)  def http_conected():    _alarm = Pin(2, Pin.OUT)    _alarm.value(0)    time.sleep_ms(100)    _alarm.value(1)    time.sleep_ms(200)    _alarm.value(0)    time.sleep_ms(400)    _alarm.value(1)    time.sleep_ms(200)      def mqtt_conected():    _alarm = Pin(2, Pin.OUT)    _alarm.value(0)    time.sleep_ms(100)    _alarm.value(1)    time.sleep_ms(100)    _alarm.value(0)    time.sleep_ms(100)    _alarm.value(1)    time.sleep_ms(100)    _alarm.value(0)    time.sleep_ms(100)    _alarm.value(1)    time.sleep_ms(200)  def request():    _alarm = Pin(2, Pin.OUT)    _alarm.value(0)    time.sleep_ms(50)    _alarm.value(1)    time.sleep_ms(200)  def error():    _alarm = Pin(2, Pin.OUT)    _alarm.value(0)    time.sleep_ms(1000)    _alarm.value(1)    time.sleep_ms(200)    _alarm.value(0)    time.sleep_ms(1000)    _alarm.value(1)    time.sleep_ms(200)    _alarm.value(0)    time.sleep_ms(1000)    _alarm.value(1)    time.sleep_ms(200)